const User = require('@database/models/User');
const crypto = require('@helpers/crypto');
const logger = require('@helpers/logger').createLogger('Database', 'magenta');

module.exports = function initAPI(db) {
	const foundUsers = null//db.get('users', null);
	const tempPassword = Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);

	// If no users are present, we generate a temporary user account and tell the user in the console
	if (!foundUsers || Object.keys(foundUsers.length)) {
		logger.newLine();
		logger.warn('=========================================================');
		logger.newLine();
		logger.warn('No registered users found in DB!');
		logger.warn(`Enabling admin account 'temp' with password '${tempPassword}'`);
		logger.newLine();
		logger.warn('=========================================================');
		logger.newLine();
	}

	const api = {
		_set: async (users) => db.set('users', users),
		async getAll() {
			return db.get('users', { 'temp': User.composeTempUser(tempPassword) });
		},
		async findUser(username) {
			return (await api.getAll())[username];
		},

		/**
		 * Update the user meta in DB (display name and avatar url).
		 *
		 * This method does NOT allow changing the password.
		 * Only do this with `api.users.updatePassword(<username>, <unhashed-password>)`.
		 *
		 * @param  {string} username Username
		 * @param  {User} user The user object
		 * @return {void}
		 */
		async updateMeta(username, { avatarUrl, displayName }) {
			let users = await api.getUsers();

			if (!users[username])
				throw new Error(`User ${username} could not be found`);

			// Password can not be changed in the update method
			users[username] = User.validate({
				...users[user.username],
				avatarUrl,
				displayName
			});

			await api._set(users);
		},

		/**
		 * Update the user meta in DB (display name and avatar url).
		 *
		 * This method does NOT allow changing the password.
		 * Only do this with `api.users.updatePassword(<username>, <unhashed-password>)`.
		 *
		 * @param  {string} username Username
		 * @param  {User} user The user object
		 * @return {void}
		 */
		async updateUsername(username, newUsername) {
			let users = await api.getUsers();

			if (!users[username])
				throw new Error(`User ${username} could not be found`);

			if (!users[newUsername])
				throw new Error(`Cannot change username. User ${newUsername} already exists.`);

			logger.info(`Changing username from ${username} to ${newUsername}`);

			const newUser = User.validate({
				...users[username],
				username: newUsername
			});

			delete users[username];
			users[newUsername] = newUser;

			await api._set(users);
		},

		/**
		 * Hash a users password and save it to DB.
		 * 
		 * @param  {string} username
		 * @param  {string} password
		 */
		async updatePassword(username, password) {
			let users = await api.getUsers();

			if (!users[username])
				throw new Error(`User ${username} could not be found`);

			users[username] = User.validate({
				...users[username],
				password: crypto.hashPasswordSync(password)
			});

			await api._set(users);
		}
	};

	return api;
}

/**
 * Database Module
 * @module @database
 * @since 1.0.0
 */
const logger = require('@helpers/logger').createLogger('Database', 'cyan');
const config = require('@config');
const fs = require('fs');

let database = {};

// If the storage folder doesnt exist, create it
if (!fs.existsSync(config.storagePath)) {
	logger.warn("Storage folder doesn't exist. Creating /storage.");
	fs.mkdirSync(config.storagePath, { recursive: true });
}

// If the database.json file doenst exist, create it
// Otherwise, read from it and populate the database.
if (!fs.existsSync(config.databasePath)) {
	fs.writeFileSync(config.databasePath, '{}');
} else {
	database = require(config.databasePath);
}

/**
 * Set an object in the database to a given value.
 *
 * This function is used to save data permanently to the database.
 * Saves on every set.
 *
 * @todo  More object setting than 1 level deep.
 *
 * @param {String} key - The database object key to query. For now, only 1 level deep.
 * @param {any} value - The value you want to set.
 */
module.exports.set = function set(key, value) {
	database[key] = value;

	// Save the database to disk
	fs.writeFileSync(
		config.databasePath,
		JSON.stringify(database, null, 2),
		err => {
			if (err) logger.error('Error writing database file', err);
		}
	);
};

/**
 * Retrieve data from a database with a given key.
 *
 * @todo  multi-level getting
 *
 * @param  {String} key - The key to retrieve from the database. (1-level deep)
 * @return {Object} The object from the database.
 */
module.exports.get = function get(key, defaultValue) {
	return database[key] || defaultValue;
};

/**
 * The database API to safely interact with the database.
 * @type {Object}
 */
module.exports.api = {
	/**
	 * The users API.
	 * @type {Object}
	 */
	users: require('./api/users')(module.exports)
};

const Joi = require('joi');

const crypto = require('@helpers/crypto');
const composeModel = require('./compose-model');

module.exports.User = composeModel(Joi.object({
	username: Joi.string()
		.alphanum()
		.min(3)
		.max(32)
		.lowercase()
		.trim()
		.required(),

	password: Joi.string(),

	displayName: Joi.string()
		.min(1)
		.max(32)
		.trim()
		.required(),

	avatarUrl: Joi.string()
		.trim()
		.max(512)
		.uri(),

	role: Joi.string()
		.pattern(/^admin$|^user$|^guest$/, 'role')
		.required()
}));

module.exports.User.composeTempUser = function composeTempUser(password) {
	return {
		username: 'temp',
		password: crypto.hashPasswordSync(tempPassword),
		displayName: 'Temp Admin',
		avatarUrl: 'https://static.wikia.nocookie.net/geosheas-lost-episodes/images/c/c0/Shrekednijrfiugbqg4iuhrqipugheipug.jpg/revision/latest?cb=20200809223801',
		role: 'admin'
	};
}

module.exports = function composeModel(schema) {
	return {
		schema,
		validate: (data) => {
			const { error, value } = schema.validate(data);

			if (error) {
				throw error;
			}

			return value;
		}
	}
}

/**
 * Express middleware to force a trailing slash onto a route.
 *
 * e.g.
 * /dashboard -> /dashboard/
 */
module.exports = function addTrailingSlashMiddleware(req, res, next) {
	if (req.url.slice(-1) !== '/') {
		res.redirect(301, req.url + '/');
	} else {
		next();
	}
}

